INSERT INTO acm_elements (id, domain, type, tool, name, definition) VALUES
	(1, 'EMO', 'generic', null, 'PERSONAL EMOTIONAL MANAGEMENT', 'Kemampuan individu untuk mengelola dan mengatur emosi pribadi dengan efektif, melibatkan kesadaran emosional, pengendalian diri, dan kemampuan untuk mengelola stres, frustrasi, dan tekanan secara sehat'),
	(2, 'EMO', 'specific', 'case-emo', 'Self-Awareness', 'Kemampuan untuk mengenali dan memahami emosi yang dirasakan, serta mengetahui bagaimana emosi tersebut mempengaruhi pikiran, perasaan, dan tindakan seseorang.'),
	(3, 'EMO', 'specific', 'case-emo', 'Self-Regulation', 'Kemampuan untuk mengendalikan dan mengatur emosi diri sendiri, sehingga dapat menangani situasi yang menantang dengan tenang dan bijaksana, serta mampu menyesuaikan diri dengan perubahan lingkungan.'),
	(4, 'EMO', 'gpq', 'gpq', 'Self-Control GPQ', 'Kemampuan untuk mengendalikan dan mengatur emosi diri sendiri, sehingga dapat menangani situasi yang menantang dengan tenang dan bijaksana, serta mampu menyesuaikan diri dengan perubahan lingkungan.'),
	(5, 'EMO', 'specific', 'case-emo', 'Stress Management', 'Kemampuan untuk mengenali, mengatasi, dan mengurangi stres dalam kehidupan sehari-hari, baik di tempat kerja maupun di luar tempat kerja. Pengendalian stres melibatkan teknik relaksasi, pemikiran positif, dan strategi penyesuaian untuk menjaga kesehatan emosional dan fisik.'),
	(6, 'EMO', 'generic', null, 'SOCIAL EMOTIONAL MANAGEMENT', 'Kemampuan individu untuk mengelola dan mengatur emosi dalam interaksi sosial dengan orang lain, melibatkan kesadaran emosional, kemampuan berempati, pengaturan diri, dan kemampuan untuk menjaga hubungan yang sehat dan bermakna dengan orang lain.'),
	(7, 'EMO', 'specific', 'lgd', 'Social Recognition', 'Kemampuan untuk mengenali dan memahami emosi orang lain melalui informasi verbal/non verbal, berbagai isyarat, seperti ekspresi wajah, bahasa tubuh, dan nada suara.'),
	(8, 'EMO', 'specific', 'lgd', 'Social Response', 'Kemampuan untuk menengarai, memahami dan merespon situasi dari sudut pandang orang lain, serta menghargai perasaan, pikiran, dan pengalaman mereka, memastikan dan menunjukkan bahwa emosi mereka valid dan penting untuk dipahami.'),
	(9, 'SELF', 'generic', null, 'ADAPTABILITY', 'Kemampuan untuk merespons dan beradaptasi dengan perubahan atau keadaan baru, melibatkan fleksibilitas dalam berpikir dan perilaku, dan kesediaan merubah pendekatan sesuai situasi.'),
	(10, 'SELF', 'gpq', 'gpq', 'ADAPTABILITY GPQ', 'Kemampuan untuk merespons dan beradaptasi dengan perubahan atau keadaan baru, melibatkan fleksibilitas dalam berpikir dan perilaku, dan kesediaan merubah pendekatan sesuai situasi.'),
	(11, 'SELF', 'specific', 'lgd', 'Openness', 'Kemampuan untuk menerima dan mempertimbangkan ide, pendapat, dan perspektif yang berbeda.'),
	(12, 'SELF', 'specific', 'wwcr', 'Mental Flexibility', 'Kemampuan untuk berpikir secara terbuka dan menerima perubahan, serta bersedia untuk mengubah pendekatan atau ide ketika situasi memerlukan.'),
	(13, 'SELF', 'specific', 'wwcr', 'Self-Introspection', 'Kemampuan menilai diri sendiri secara kritis, termasuk kekuatan, kelemahan, dan bias, serta pengaruhnya pada pengambilan keputusan dan tindakan.'),
	(14, 'SELF', 'specific', 'wwcr', 'Ambiguity Tolerance', 'Kemampuan untuk merasa nyaman dalam menghadapi ketidakpastian dan situasi yang tidak jelas, serta mampu bekerja efektif dalam kondisi tersebut.'),
	(15, 'SELF', 'specific', 'wwcr', 'Work-Life Balance', 'Kemampuan mempertahankan keseimbangan antara kehidupan profesional dan pribadi, meliputi pengaturan waktu, prioritas, dan komitmen yang sehat.'),
	(16, 'SELF', 'generic', null, 'ACHIEVEMENT DRIVE', 'Motivasi intrinsik seseorang untuk mencapai dan melampaui tujuan, menjaga standar kerja yang tinggi, dan secara konsisten berusaha mencapai kinerja pribadi dan profesional yang lebih baik.'),
	(17, 'SELF', 'gpq', 'gpq', 'ACHIEVEMENT DRIVE GPQ', 'Motivasi intrinsik seseorang untuk mencapai dan melampaui tujuan, menjaga standar kerja yang tinggi, dan secara konsisten berusaha mencapai kinerja pribadi dan profesional yang lebih baik.'),
	(18, 'SELF', 'specific', 'wwcr', 'Resilience (Kemembalan)', 'Kemampuan untuk cepat pulih mengatasi kegagalan, rintangan, tekanan, melibatkan ketahanan emosional, fleksibilitas, dan kemampuan untuk belajar dari kesalahan.'),
	(19, 'SELF', 'specific', 'wwcr', 'Persistence (Ketekunan)', 'Kemampuan untuk terus berusaha mencapai tujuan meskipun dihadapkan dengan kesulitan atau penundaan dalam mencapai sukses, fokus pada aksi dan upaya yang berkelanjutan.'),
	(20, 'SELF', 'specific', 'wwcr', 'Determination (Tekad)', 'Kemampuan kekuatan mental dan kemauan untuk mencapai tujuan, tidak peduli seberapa sulit rintangannya, fokus pada motivasi intrinsik yang kuat dan hasil.'),
	(21, 'SELF', 'specific', 'wwcr', 'Initiative', 'Kemampuan untuk mengambil tindakan proaktif dan mengidentifikasi peluang tanpa menunggu perintah atau arahan dari orang lain, melibatkan antusiasme, kreativitas, dan keinginan untuk mencoba hal-hal baru serta menghadapi tantangan.'),
	(22, 'SELF', 'specific', 'wwcr', 'Independence', 'Kemampuan bekerja dan membuat keputusan secara mandiri, tanpa tergantung pada bantuan atau arahan orang lain, keyakinan pada kemampuan diri, tanggung jawab atas tindakan dan kemampuan mengelola secara efisien.'),
	(23, 'SELF', 'specific', 'wwcr', 'Goal Setting', 'Kemampuan menetapkan, melacak, dan mencapai tujuan yang bermakna dan realistis, melibatkan pemahaman apa yang ingin dicapai, penentuan langkah-langkah konkret dan komitmen.'),
	(24, 'SELF', 'specific', 'wwcr', 'Solution-Oriented', 'Kemampuan untuk mencari dan mengembangkan solusi yang efektif ketika menghadapi perubahan dan ketidakpastian, serta memanfaatkan peluang yang muncul dari situasi yang dinamis.'),
	(25, 'SELF', 'specific', 'wwcr', 'Results-Oriented', 'Fokus pada pencapaian hasil yang diinginkan dan melampaui standar kinerja yang ada, melibatkan komitmen untuk mengukur kemajuan, menganalisis kesenjangan, dan mengambil tindakan.'),
	(26, 'SELF', 'generic', null, 'CONTINUOUS LEARNING', 'kemampuan seseorang untuk secara proaktif dan dengan niatan belajar secara terus-menerus, untuk mengembangkan dan meningkatkan pengetahuan, keterampilan, dan kompetensi sepanjang karier dan hidup mereka, melibatkan penyesuaian diri dengan perubahan, serta kemampuan untuk memanfaatkan pengalaman dan kesalahan sebagai peluang untuk belajar dan tumbuh.'),
	(27, 'SELF', 'specific', 'lgd', 'Curiosity', 'Sikap yang terus-menerus ingin mengetahui, mempelajari, dan mengeksplorasi hal-hal baru, menggali lebih dalam untuk memahami topik atau situasi.'),
	(28, 'SELF', 'specific', 'wwcr', 'Knowledge Seeking', 'Usaha yang aktif dan terus-menerus untuk memperoleh pengetahuan baru, baik melalui pembelajaran formal maupun informal.'),
	(29, 'SELF', 'specific', 'wwcr', 'Experimentation', 'Kemampuan untuk mencoba pendekatan baru, teknik, atau alat dalam rangka mencari solusi yang lebih baik, belajar dari kegagalan dan kesuksesan.'),
	(30, 'COG', 'generic', null, 'CONCRETE THINKING', 'Kemampuan untuk berpikir tentang objek, situasi, atau peristiwa secara literal dan dalam bentuk yang dapat diamati atau dirasakan, melibatkan pemikiran yang fokus pada fakta-fakta nyata, detail-detail spesifik, dan data yang jelas dan dapat diverifikasi'),
	(31, 'COG', 'generic', null, 'CONCEPTUAL THINKING', 'Kemampuan untuk memahami ide-ide yang kompleks atau abstrak dan mengidentifikasi pola atau koneksi antara situasi yang tampaknya tidak berhubungan secara langsung, melibatkan berpikir pada level yang lebih tinggi, di luar hal yang konkrit, detail dan situasi nyata.'),
	(32, 'COG', 'gpq', 'gpq', 'CONCEPTUAL THINKING GPQ', 'Kemampuan untuk memahami ide-ide yang kompleks atau abstrak dan mengidentifikasi pola atau koneksi antara situasi yang tampaknya tidak berhubungan secara langsung, melibatkan berpikir pada level yang lebih tinggi, di luar hal yang konkrit, detail dan situasi nyata.'),
	(33, 'COG', 'generic', null, 'ANALYTICAL THINKING', 'Merujuk pada kemampuan untuk mendekomposisi informasi kompleks menjadi bagian yang lebih kecil, memilah unsur-unsur yang membentuk kondisi tersebut, menganalisis keterkaitan antara unsur-unsur tersebut dalam rangka memahami pola atau tren.'),
	(34, 'COG', 'gpq', 'gpq', 'ANALYTICAL THINKING GPQ', 'Kemampuan untuk berpikir tentang objek, situasi, atau peristiwa secara literal dan dalam bentuk yang dapat diamati atau dirasakan, melibatkan pemikiran yang fokus pada fakta-fakta nyata, detail-detail spesifik, dan data yang jelas dan dapat diverifikasi'),
	(35, 'COG', 'specific', 'lgd', 'Argumentation', 'Kemampuan untuk menggunakan argumen, logika dan alasan dalam mengevaluasi informasi dan membuat keputusan.'),
	(36, 'COG', 'specific', 'case-cog', 'Abstract Reasoning', 'Kemampuan untuk memahami konsep dan ide yang tidak terikat secara langsung oleh pengalaman sensoris dan konkret. Ini melibatkan kemampuan untuk melihat pola dan hubungan, berpikir secara hipotetis, dan menguasai konsep yang kompleks dan abstrak.'),
	(37, 'COG', 'specific', 'case-cog', 'Information Synthesis', 'Kemampuan untuk menggabungkan informasi dari berbagai sumber, baik konkret maupun abstrak, untuk menciptakan pemahaman yang lebih dalam atau mencari solusi yang lebih baik untuk suatu masalah.'),
	(38, 'COG', 'specific', 'case-cog', 'Evidence Evaluation', 'Kemampuan untuk menilai kualitas dan relevansi bukti sebelum menggunakannya dalam pengambilan keputusan.'),
	(39, 'COG', 'specific', 'case-cog', 'Conclusion Drawing ', 'Kemampuan untuk mengambil kesimpulan yang tepat dan relevan berdasarkan analisis dan interpretasi data.'),
	(40, 'COG', 'specific', 'case-cog', 'Bias Identification', 'Kemampuan untuk mengidentifikasi dan mengakui bias dalam informasi, proses pemikiran, dan perspektif orang lain serta diri sendiri.'),
	(41, 'COG', 'specific', 'case-cog', 'Concept Understanding', 'Kemampuan untuk memahami konsep-konsep abstrak dan penerapan pemahaman tersebut dalam situasi nyata.'),
	(42, 'COG', 'specific', 'case-cog', 'Concept Application', 'Kemampuan untuk menggabungkan konsep-konsep yang telah dipahami dalam menghadapi situasi baru, dan menciptakan solusi yang efektif.'),
	(43, 'COG', 'generic', null, 'PROBLEM SOLVING', 'kemampuan untuk mengidentifikasi, menganalisis, dan menemukan solusi untuk masalah.'),
	(44, 'COG', 'specific', 'case-cog', 'Problem Identification', 'Kemampuan untuk mengenali dan mendefinisikan masalah yang dihadapi serta menentukan penyebab utamanya.'),
	(45, 'COG', 'specific', 'case-cog', 'Problem Anticipation', 'Kemampuan untuk mengantisipasi dan mengidentifikasi potensi masalah sebelum mereka terjadi, memungkinkan tindakan pencegahan yang efektif.'),
	(46, 'COG', 'specific', 'case-cog', 'Problem Analysis', 'Kemampuan untuk menganalisis masalah secara sistematis, mengumpulkan informasi yang relevan, dan mengidentifikasi akar penyebab masalah.'),
	(47, 'COG', 'specific', 'case-cog', 'Risk Management', 'Kemampuan untuk mengidentifikasi, mengevaluasi, dan mengelola risiko yang mungkin mempengaruhi pencapaian tujuan atau operasi suatu organisasi.'),
	(48, 'COG', 'specific', 'case-cog', 'Analisis SWOT (SWOT Analysis)', 'Kemampuan untuk menganalisis kekuatan, kelemahan, peluang, dan ancaman organisasi atau situasi tertentu untuk mendukung pengambilan keputusan strategis.'),
	(49, 'COG', 'specific', 'case-cog', 'PESTEL Analysis', 'Kemampuan untuk menganalisis faktor politik, ekonomi, sosial, teknologi, lingkungan, dan hukum yang mempengaruhi organisasi atau situasi tertentu.'),
	(50, 'COG', 'generic', null, 'DECISION MAKING', 'Kemampuan untuk membuat keputusan yang efektif dan tepat waktu, seringkali di bawah tekanan dan dengan informasi yang terbatas atau tidak pasti.'),
	(51, 'COG', 'specific', 'case-cog', 'Alternative Evaluation', 'Kemampuan untuk mengevaluasi alternatif yang berbeda berdasarkan analisis data dan memilih opsi terbaik.'),
	(52, 'CREA', 'generic', null, 'CREATIVE THINKING', 'merujuk pada kemampuan untuk memikirkan ide-ide baru dan asli, dan menciptakan solusi inovatif untuk masalah yang ada, melibatkan melihat dunia dengan cara baru, memanfaatkan imajinasi, dan menghargai perspektif dan ide yang berbeda.'),
	(53, 'CREA', 'gpq', 'gpq', 'CREATIVE THINKING GPQ', 'merujuk pada kemampuan untuk memikirkan ide-ide baru dan asli, dan menciptakan solusi inovatif untuk masalah yang ada, melibatkan melihat dunia dengan cara baru, memanfaatkan imajinasi, dan menghargai perspektif dan ide yang berbeda.'),
	(54, 'CREA', 'generic', null, 'INNOVATIVE THINKING', 'Kemampuan untuk menghasilkan ide-ide baru dan mencari cara-cara baru untuk memecahkan masalah dan meningkatkan efisiensi dan efektivitas, melibatkan pemanfaatan kreativitas, pengetahuan, dan keterampilan teknis untuk mengembangkan solusi atau pendekatan baru yang belum pernah ada sebelumnya.'),
	(55, 'CREA', 'specific', 'case-cog', 'Thinking Outside the Box', 'Kemampuan untuk melihat masalah atau situasi dari sudut pandang yang berbeda, dan mencari solusi yang tidak konvensional atau inovatif.'),
	(56, 'CREA', 'specific', 'case-cog', 'Ideation', 'Kemampuan untuk menghasilkan sejumlah ide baru, unik, dan bervariasi dalam menghadapi masalah atau tantangan.'),
	(57, 'CREA', 'specific', 'case-cog', 'Free Association', 'Kemampuan untuk menghubungkan ide-ide yang tampaknya tidak terkait, dengan tujuan untuk menghasilkan ide-ide baru dan kreatif.'),
	(58, 'STRAT', 'generic', null, 'STRATEGIC THINKING', 'Kemampuan berpikir kritis tentang gambaran besar dan kompleks, mengembangkan visi untuk masa depan, dan rencana untuk mencapai visi tersebut, menganalisis situasi yang kompleks, memahami dan meramalkan tren, membuat keputusan dan mendorong perubahan jangka panjang.'),
	(59, 'STRAT', 'gpq', 'gpq', 'STRATEGIC THINKING GPQ', 'Kemampuan berpikir kritis tentang gambaran besar dan kompleks, mengembangkan visi untuk masa depan, dan rencana untuk mencapai visi tersebut, menganalisis situasi yang kompleks, memahami dan meramalkan tren, membuat keputusan dan mendorong perubahan jangka panjang.'),
	(60, 'STRAT', 'generic', null, 'FORECASTING', 'Kemampuan untuk membuat perkiraan tentang perkembangan masa depan berdasarkan analisis tren dan pemahaman tentang lingkungan bisnis.'),
	(61, 'STRAT', 'generic', null, 'STRATEGIC ANALYSIS', 'Kemampuan menganalisis berbagai elemen organisasi atau situasi untuk membuat keputusan, strategi, dan memprediksi sebuah potensi, melibatkan pemahaman yang mendalam tentang lingkungan bisnis, tren pasar, kekuatan dan kelemahan organisasi, serta risiko dan peluang potensial.'),
	(62, 'STRAT', 'specific', 'case-cog', 'Systems Thinking', 'kemampuan untuk melihat dan memahami hubungan antara bagian-bagian dari sistem dan bagaimana mereka saling berinteraksi dan berkontribusi pada sistem secara keseluruhan. Berpikir sistemik adalah pendekatan holistik yang melihat sistem sebagai suatu kesatuan, bukan hanya sebagai kumpulan bagian yang terisolasi.'),
	(63, 'STRAT', 'specific', 'case-cog', 'Vision Development', 'Kemampuan untuk menciptakan dan menggambarkan visi jangka panjang yang inspiratif dan menarik, yang dapat membimbing organisasi atau tim menuju kesuksesan di masa depan.'),
	(64, 'STRAT', 'specific', 'case-cog', 'Trend Analysis', 'kemampuan untuk memahami dan memprediksi arah perubahan dalam data sepanjang waktu dan memahami dampak dari tren tersebut terhadap situasi atau organisasi saat ini dan masa depan'),
	(65, 'NORM', 'generic', null, 'INTEGRITY', 'Kemampuan teguh pada prinsip dan tindakan yang jujur, etis, dan konsisten, memegang teguh nilai-nilai moral dan prinsip-prinsip yang benar, dan bertindak dengan konsistensi antara apa yang mereka katakan dan apa yang mereka lakukan.'),
	(66, 'NORM', 'generic', null, 'ACCOUNTABILITY', 'Tanggung jawab untuk hasil kerja, keputusan yang diambil, dan tindakan yang dilakukan, serta berkomitmen untuk memperbaiki dan belajar dari kesalahan atau kekurangan.'),
	(67, 'NORM', 'specific', 'wwcr', 'Ethical Awareness', 'Kemampuan untuk mengenali dan memahami nilai-nilai etika, norma, dan standar yang berlaku dalam suatu organisasi, profesi, atau masyarakat, serta menjadikannya sebagai pedoman dalam pengambilan keputusan dan tindakan.'),
	(68, 'NORM', 'specific', 'wwcr', 'Honesty and Transparency', 'Komitmen untuk selalu bersikap jujur, terbuka, dan transparan dalam semua tindakan dan komunikasi, baik dalam hubungan internal maupun eksternal, sehingga dapat membangun kepercayaan dan kredibilitas.'),
	(69, 'NORM', 'specific', 'wwcr', 'Value Alignment', 'Kesesuaian nilai-nilai, keyakinan, dan tindakan pribadi dengan nilai-nilai, visi, dan misi organisasi, serta berkomitmen untuk mendukung dan mewujudkan tujuan organisasi.'),
	(70, 'NORM', 'specific', 'wwcr', 'Adherence to Principles', 'Kemampuan untuk berpegang teguh pada prinsip-prinsip etika dan integritas dalam situasi yang sulit atau menantang, serta mampu menegakkan standar etika yang tinggi dalam diri sendiri dan orang lain.'),
	(71, 'NORM', 'specific', 'wwcr', 'Environmental Sustainability', 'Komitmen untuk menjaga dan melindungi lingkungan, serta mengadopsi praktik bisnis yang ramah lingkungan dan berkelanjutan, dengan tujuan untuk menciptakan keseimbangan antara pertumbuhan ekonomi, kesejahteraan sosial, dan kelestarian lingkungan.'),
	(72, 'NORM', 'specific', 'wwcr', 'Social Responsibility', 'Kesadaran akan dampak tindakan dan keputusan yang diambil terhadap masyarakat dan lingkungan, serta berkomitmen untuk berkontribusi pada kesejahteraan sosial dan pembangunan yang berkelanjutan.'),
	(73, 'COM', 'generic', null, 'COMMUNICATION', 'kemampuan untuk secara efektif menyampaikan dan menerima informasi, baik secara lisan maupun tertulis, melibatkan pemahaman tentang bagaimana cara terbaik untuk menyampaikan pesan kepada berbagai audiens dan bagaimana mendengarkan dan memahami pesan orang lain.'),
	(74, 'COM', 'gpq', 'gpq', 'COMMUNICATION GPQ', 'kemampuan untuk secara efektif menyampaikan dan menerima informasi, baik secara lisan maupun tertulis, melibatkan pemahaman tentang bagaimana cara terbaik untuk menyampaikan pesan kepada berbagai audiens dan bagaimana mendengarkan dan memahami pesan orang lain.'),
	(75, 'COM', 'specific', 'pres', 'Visual Communication', 'Kemampuan untuk menyampaikan temuan dan wawasan yang diperoleh dari visualisasi data kepada audiens yang dituju, dengan cara yang jelas, ringkas, dan persuasif, serta memastikan bahwa pesan yang disampaikan mudah dipahami dan relevan dengan kepentingan audiens.'),
	(76, 'COM', 'specific', 'wwcr', 'Vision & Strategy Communication', 'Kemampuan untuk menyampaikan visi dan strategi kepada anggota tim atau organisasi secara jelas, persuasif, dan inspiratif, sehingga mereka dapat memahami dan mendukung tujuan bersama.'),
	(77, 'COM', 'specific', 'pres', 'Information Sharing', 'Kemampuan untuk berbagi informasi, pengetahuan, dan sumber daya dengan orang lain dalam jaringan secara efektif.'),
	(78, 'COM', 'specific', 'lgd', 'Speaking Skills', 'Kemampuan untuk menyampaikan informasi dan ide secara jelas dan efektif melalui lisan.'),
	(79, 'COM', 'specific', 'lgd', 'Effective Response', 'Kemampuan untuk memberikan tanggapan yang efektif dan tepat waktu terhadap komunikasi dari orang lain.'),
	(80, 'COM', 'specific', 'lgd', 'Communication Skills', 'Kemampuan untuk menyesuaikan gaya komunikasi sesuai dengan konteks dan audiens yang berbeda, mampu menyampaikan informasi dengan jelas dan efektif, serta memahami perspektif dan kebutuhan orang lain.'),
	(81, 'COM', 'specific', 'lgd', 'Active Listening', 'Kemampuan untuk secara efektif menerima dan memahami informasi yang disampaikan oleh orang lain.'),
	(82, 'COM', 'specific', 'pres', 'Writing Skills', 'Kemampuan untuk menyampaikan informasi dan ide secara jelas dan efektif melalui tulisan.'),
	(83, 'SOC', 'generic', null, 'NETWORKING', 'kemampuan seseorang untuk menciptakan, membina, dan memanfaatkan hubungan dengan orang lain untuk keuntungan bersama, terutama dalam konteks profesional.'),
	(84, 'SOC', 'gpq', 'gpq', 'NETWORKING GPQ', 'kemampuan seseorang untuk menciptakan, membina, dan memanfaatkan hubungan dengan orang lain untuk keuntungan bersama, terutama dalam konteks profesional.'),
